class IKit::TestIkit::Parser::Runner
  def initialize(search_string)
    @search_string = search_string
    @country       = 'RUS'
    @url           = 'https://certification.pmi.org/registry.aspx'
  end

  def save_resources!
    page = Nokogiri::HTML(get_html.to_s)
    attributes = IKit::TestIkit::Parser::Page.new(page).get_attributes
    #TODO plase for stroing staff
  end

  def get_html
    cmd = "curl 'https://certification.pmi.org/registry.aspx' -H 'Accept: text/html,application/xhtml+xml,application/xml;q=0.9,image/webp,*/*;q=0.8' -H 'Referer: https://certification.pmi.org/registry.aspx' -H 'Origin: https://certification.pmi.org' -H 'Upgrade-Insecure-Requests: 1' -H 'User-Agent: Mozilla/5.0 (Macintosh; Intel Mac OS X 10_9_5) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/46.0.2490.71 Safari/537.36' -H 'Content-Type: application/x-www-form-urlencoded' --data '__EVENTTARGET=&__EVENTARGUMENT=&__VIEWSTATE=%%%3D&Header1%24SearchText=&dph_RegistryContent%24tbSearch=#{@search_string}&dph_RegistryContent%24firstNameTextBox=&dph_RegistryContent%24wcountry=#{@country}&dph_RegistryContent%24credentialDDL=0&dph_RegistryContent%24Button1=Search' --compressed"
    `#{cmd}`
  end

  def get_html2 #TODO разобраться почему запрос не возвращает таблицу.. 
    uri   = URI(@url)
    https = Net::HTTP.new(uri.host, uri.port)
    https.use_ssl = true
    request = https.post(uri, data.to_json, headers)

    gz = Zlib::GzipReader.new(StringIO.new(request.body.to_s))    
    uncompressed_string = gz.read
  end

  def headers
    @headers ||= {
      'Origin' => 'https://certification.pmi.org',
      'Accept-Encoding' => 'gzip, deflate',
      'Accept-Language' => 'ru-RU,ru;q=0.8,en-US;q=0.6,en;q=0.4',
      'Upgrade-Insecure-Requests' => '1',
      'User-Agent' => 'Mozilla/5.0 (Macintosh; Intel Mac OS X 10_11_0) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/46.0.2490.71 Safari/537.36',
      'Content-Type' => 'application/x-www-form-urlencoded',
      'Accept' => 'text/html,application/xhtml+xml,application/xml;q=0.9,image/webp,*/*;q=0.8',
      'Cache-Control' => 'no-cache',
      'Referer' => 'https://certification.pmi.org/registry.aspx',
      'Cookie' => 'ASP.NET_SessionId=ajdojm55hpf5yfnh3i1e2r45',
      'Connection' => 'keep-alive',
      }
  end

  def data
    @data ||= {
      '__EVENTTARGET' => nil,
      '__EVENTARGUMENT' => nil,
      '__VIEWSTATE' => "",
      'Header1$SearchText' => nil,
      'dph_RegistryContent$tbSearch' => @search_string,
      'dph_RegistryContent$firstNameTextBox' => nil,
      'dph_RegistryContent$wcountry' => @country,
      'dph_RegistryContent$credentialDDL' => 0,
      'dph_RegistryContent$Button1' => 'Search',
    }
  end

end
